    "caretBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowCluster",
                name = "caret",
                version = c(1,0,0),
                options = options,
                results = caretResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE)
        }))

#' Machine Learning
#'
#' 
#' @param data .
#' @param dep .
#' @param covs the covariates from \code{data}
#' @param facs the fixed factors from \code{data}
#' @param mecon .
#' @param method .
#' @param cm1 .
#' @param number .
#' @param repeats .
#' @param tune .
#' @param per .
#' @param over1 .
#' @param over .
#' @param cla .
#' @param cla1 .
#' @param tra .
#' @param tes .
#' @param plot .
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param plot4 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$over1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$over} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tra} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tes} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cla1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cla} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$over1$asDF}
#'
#' \code{as.data.frame(results$over1)}
#'
#' @export
caret <- function(
    data,
    dep,
    covs = NULL,
    facs = NULL,
    mecon,
    method = "pls",
    cm1 = "ctree",
    number = 10,
    repeats = 5,
    tune = 10,
    per = 0.7,
    over1 = TRUE,
    over = FALSE,
    cla = FALSE,
    cla1 = FALSE,
    tra = TRUE,
    tes = FALSE,
    plot = FALSE,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    plot4 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("caret requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if ( ! missing(facs)) facs <- jmvcore::resolveQuo(jmvcore::enquo(facs))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(covs), covs, NULL),
            `if`( ! missing(facs), facs, NULL))

    for (v in dep) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in facs) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- caretOptions$new(
        dep = dep,
        covs = covs,
        facs = facs,
        mecon = mecon,
        method = method,
        cm1 = cm1,
        number = number,
        repeats = repeats,
        tune = tune,
        per = per,
        over1 = over1,
        over = over,
        cla = cla,
        cla1 = cla1,
        tra = tra,
        tes = tes,
        plot = plot,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        plot4 = plot4)

    analysis <- caretClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

