#' Correspondence Analysis
#'
#' 
#' @param data The data as a data frame.
#' @param labels .
#' @param vars .
#' @param chi .
#' @param eigen .
#' @param loadingvar .
#' @param loadingind .
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param plot4 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$chi} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$eigen} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$loadingvar} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$loadingind} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$chi$asDF}
#'
#' \code{as.data.frame(results$chi)}
#'
#' @export
correspondence <- function(
    data,
    labels,
    vars,
    chi = TRUE,
    eigen = FALSE,
    loadingvar = FALSE,
    loadingind = FALSE,
    plot1 = TRUE,
    plot2 = FALSE,
    plot3 = FALSE,
    plot4 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("correspondence requires jmvcore to be installed (restart may be required)")

    if ( ! missing(labels)) labels <- jmvcore::resolveQuo(jmvcore::enquo(labels))
    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(labels), labels, NULL),
            `if`( ! missing(vars), vars, NULL))

    for (v in vars) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- correspondenceOptions$new(
        labels = labels,
        vars = vars,
        chi = chi,
        eigen = eigen,
        loadingvar = loadingvar,
        loadingind = loadingind,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        plot4 = plot4)

    analysis <- correspondenceClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

