#' Factor analysis of mixed data
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param labels .
#' @param eigen .
#' @param rowvar .
#' @param colvar .
#' @param quanvar .
#' @param qualvar .
#' @param ci .
#' @param cg .
#' @param quanti .
#' @param qual .
#' @param plot .
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param plot4 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$eigen} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$ci} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cg} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$quanti} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$qual} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$eigen$asDF}
#'
#' \code{as.data.frame(results$eigen)}
#'
#' @export
famd <- function(
    data,
    vars,
    labels,
    eigen = TRUE,
    rowvar = "coordinates",
    colvar = "coordinates",
    quanvar = "coordinates",
    qualvar = "coordinates",
    ci = FALSE,
    cg = FALSE,
    quanti = FALSE,
    qual = FALSE,
    plot = TRUE,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    plot4 = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("famd requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(labels)) labels <- jmvcore::resolveQuo(jmvcore::enquo(labels))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(labels), labels, NULL))


    options <- famdOptions$new(
        vars = vars,
        labels = labels,
        eigen = eigen,
        rowvar = rowvar,
        colvar = colvar,
        quanvar = quanvar,
        qualvar = qualvar,
        ci = ci,
        cg = cg,
        quanti = quanti,
        qual = qual,
        plot = plot,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        plot4 = plot4)

    analysis <- famdClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

