#' Group plot
#'
#' 
#' @param data The data as a data frame.
#' @param facs .
#' @param vars .
#' @param plot .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' @export
group <- function(
    data,
    facs,
    vars,
    plot = TRUE) {

    if ( ! requireNamespace('jmvcore'))
        stop('group requires jmvcore to be installed (restart may be required)')

    if ( ! missing(facs)) facs <- jmvcore::resolveQuo(jmvcore::enquo(facs))
    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(facs), facs, NULL),
            `if`( ! missing(vars), vars, NULL))

    for (v in facs) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- groupOptions$new(
        facs = facs,
        vars = vars,
        plot = plot)

    analysis <- groupClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
