#' K-means clustering
#'
#' 
#' @param data .
#' @param vars .
#' @param k .
#' @param algo .
#' @param nstart .
#' @param plot .
#' @param stand .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text2} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text3} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$clustering} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$centroids} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$clustering$asDF}
#'
#' \code{as.data.frame(results$clustering)}
#'
#' @export
kmeans <- function(
    data,
    vars,
    k = 2,
    algo = "Hartigan-Wong",
    nstart = 10,
    plot = TRUE,
    stand = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('kmeans requires jmvcore to be installed (restart may be required)')

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL))


    options <- kmeansOptions$new(
        vars = vars,
        k = k,
        algo = algo,
        nstart = nstart,
        plot = plot,
        stand = stand)

    analysis <- kmeansClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
