#' Multiple Factor Analysis
#'
#' 
#' @param data The data as a data frame.
#' @param labels .
#' @param vars .
#' @param group .
#' @param type .
#' @param gn .
#' @param eigen .
#' @param cg .
#' @param cq .
#' @param cqual .
#' @param plot .
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param plot4 .
#' @param plot5 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$eigen} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cg} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cq} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cqual} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$eigen$asDF}
#'
#' \code{as.data.frame(results$eigen)}
#'
#' @export
mfa <- function(
    data,
    labels,
    vars,
    group = "1,3,4,3",
    type = "n,s,s,s",
    gn = "type,rater1,rater2,rater3",
    eigen = TRUE,
    cg = FALSE,
    cq = FALSE,
    cqual = FALSE,
    plot = FALSE,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    plot4 = FALSE,
    plot5 = FALSE) {

    if ( ! requireNamespace('jmvcore'))
        stop('mfa requires jmvcore to be installed (restart may be required)')

    if ( ! missing(labels)) labels <- jmvcore::resolveQuo(jmvcore::enquo(labels))
    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(labels), labels, NULL),
            `if`( ! missing(vars), vars, NULL))


    options <- mfaOptions$new(
        labels = labels,
        vars = vars,
        group = group,
        type = type,
        gn = gn,
        eigen = eigen,
        cg = cg,
        cq = cq,
        cqual = cqual,
        plot = plot,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        plot4 = plot4,
        plot5 = plot5)

    analysis <- mfaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
